document.addEventListener("DOMContentLoaded", function () {
    var aceitarCookieButton = document.getElementById("aceitar-cookie");
    if (aceitarCookieButton) {
        aceitarCookieButton.addEventListener("click", function () {
            var bannerCookie = document.getElementById("banner-cookie");
            if (bannerCookie) {
                bannerCookie.parentNode.removeChild(bannerCookie);
            }
            definirCookie("cookie_aceito", "sim", 14);
        });
    }
});

function definirCookie(nome, valor, diasParaExpirar) {
    var dataExpiracao = new Date();
    dataExpiracao.setTime(dataExpiracao.getTime() + (diasParaExpirar * 24 * 60 * 60 * 1000));
    var expires = "expires=" + dataExpiracao.toUTCString();
    document.cookie = nome + "=" + valor + "; " + expires + "; path=/";
}
